    twitter2 = twitter.map do |n| 
        n.length
    end

    compteur = 0
    refBase = twitter2[0]
    x = twitter2.length

    for i in 0...x
            if refBase != twitter2[i]
                puts "Handle size = #{refBase} total = #{compteur}"
                refBase = twitter2[i]
                compteur = 0
            end 
            compteur += 1
    end
    puts "Handle size = #{refBase} total = #{compteur}"
end

